import{_ as a}from"./plugin-vue_export-helper-DlAUqK2U.js";import{c as i,a as n,o as t}from"./app-BgW4c22-.js";const o={};function s(l,e){return t(),i("div",null,[...e[0]||(e[0]=[n('<h1 id="companies-management-training-guide" tabindex="-1"><a class="header-anchor" href="#companies-management-training-guide"><span>Companies Management Training Guide</span></a></h1><h2 id="overview" tabindex="-1"><a class="header-anchor" href="#overview"><span>Overview</span></a></h2><p>The Companies page allows you to manage all business entities in your CRM system. Companies can be clients, suppliers, partners, or any organization you do business with. This guide covers how to add, edit, search, and manage company records.</p><hr><h2 id="accessing-the-companies-page" tabindex="-1"><a class="header-anchor" href="#accessing-the-companies-page"><span>Accessing the Companies Page</span></a></h2><p>Navigate to: <strong>Admin → CRM → Companies</strong></p><p>The Companies page displays all registered companies in a table format with the following information:</p><ul><li>Company ID</li><li>Trading Name</li><li>Address (full location)</li><li>Edit action button</li></ul><hr><h2 id="adding-a-new-company" tabindex="-1"><a class="header-anchor" href="#adding-a-new-company"><span>Adding a New Company</span></a></h2><h3 id="step-1-open-the-add-company-form" tabindex="-1"><a class="header-anchor" href="#step-1-open-the-add-company-form"><span>Step 1: Open the Add Company Form</span></a></h3><ol><li>Click the <strong>&quot;Add Company&quot;</strong> button (top right)</li><li>The Add Company modal will open</li></ol><h3 id="step-2-select-company-classification" tabindex="-1"><a class="header-anchor" href="#step-2-select-company-classification"><span>Step 2: Select Company Classification</span></a></h3><p><strong>Company Type</strong> (Required):</p><ul><li>Select the appropriate company type from the dropdown</li><li>This categorizes the company within your system</li></ul><p><strong>Organisation Type</strong> (Required):<br> Choose from the following options:</p><ul><li>Sole Proprietorship</li><li>Partnership</li><li>Private Company</li><li>Public Company</li><li>Non-Profit Organization</li><li>Government Entity</li><li>Other</li></ul><h3 id="step-3-search-and-select-address" tabindex="-1"><a class="header-anchor" href="#step-3-search-and-select-address"><span>Step 3: Search and Select Address</span></a></h3><p>The system uses intelligent address search to ensure accurate location data.</p><p><strong>Address Search</strong>:</p><ol><li>Type the company address in the search field</li><li>Suggestions will appear as you type</li><li>Click on the correct address from the suggestions</li><li>The system automatically captures: <ul><li>Full address string</li><li>Latitude coordinates</li><li>Longitude coordinates</li></ul></li></ol><p><strong>Enable Global Search</strong>:</p><ul><li>Toggle this checkbox ON to search for international addresses</li><li>Toggle OFF (default) to search only local addresses</li></ul><p><strong>Note:</strong> The address is crucial for location-based features and mapping.</p><h3 id="step-4-enter-company-details" tabindex="-1"><a class="header-anchor" href="#step-4-enter-company-details"><span>Step 4: Enter Company Details</span></a></h3><p><strong>Required Fields</strong>:</p><ol><li><p><strong>Trading Name</strong></p><ul><li>The name the company operates under</li><li>As you type, a URL-friendly slug is automatically generated</li><li>Example: &quot;ABC Travel Agency&quot; becomes &quot;abc-travel-agency&quot;</li></ul></li><li><p><strong>Slug</strong></p><ul><li>Auto-generated from Trading Name</li><li>Cannot be edited manually</li><li>Used for URLs and system references</li></ul></li><li><p><strong>Company Email</strong></p><ul><li>Primary contact email for the company</li><li>Must be a valid email format</li></ul></li><li><p><strong>Company Phone Number</strong></p><ul><li>Main contact number for the company</li></ul></li></ol><p><strong>Optional Fields</strong>:</p><ol><li><p><strong>Company Registration Number</strong></p><ul><li>Official registration/incorporation number</li><li>Leave blank if not applicable</li></ul></li><li><p><strong>VAT Registration</strong></p><ul><li>Check the &quot;VAT Registered?&quot; checkbox if company is VAT registered</li><li>VAT Number field becomes enabled when checked</li><li>Enter the VAT number if applicable</li></ul></li><li><p><strong>Website</strong></p><ul><li>Company website URL</li><li>As you type, the system extracts the top-level domain (TLD)</li><li>TLD field is auto-populated and cannot be edited</li><li>Example: &quot;<a href="https://www.example.com" target="_blank" rel="noopener noreferrer">https://www.example.com</a>&quot; → TLD: &quot;.com&quot;</li></ul></li></ol><h3 id="step-5-submit" tabindex="-1"><a class="header-anchor" href="#step-5-submit"><span>Step 5: Submit</span></a></h3><ol><li>Review all entered information</li><li>Click <strong>Submit</strong></li><li>The new company appears in your companies table</li></ol><hr><h2 id="editing-an-existing-company" tabindex="-1"><a class="header-anchor" href="#editing-an-existing-company"><span>Editing an Existing Company</span></a></h2><h3 id="opening-the-edit-form" tabindex="-1"><a class="header-anchor" href="#opening-the-edit-form"><span>Opening the Edit Form</span></a></h3><ol><li>Locate the company in the table</li><li>Click the <strong>edit icon (pen)</strong> in the rightmost column</li><li>The edit modal opens with the header &quot;Editing [Company Name]&quot;</li></ol><h3 id="updating-company-information" tabindex="-1"><a class="header-anchor" href="#updating-company-information"><span>Updating Company Information</span></a></h3><p>All fields from the Add Company form are available for editing.</p><p><strong>Changing the Company Address</strong>:</p><ol><li>Toggle <strong>&quot;Change Address&quot;</strong> switch to ON</li><li>The address search field appears</li><li>Use the same address search process as adding a company</li><li>Select the new address from suggestions</li><li>Current address, latitude, and longitude are displayed below for reference</li></ol><p><strong>Other Editable Fields</strong>:</p><ul><li>Trading Name (slug updates automatically)</li><li>Company Registration Number</li><li>Organisation Type</li><li>VAT Registration status and number</li><li>Company Email</li><li>Phone Number</li><li>Website</li></ul><p><strong>Note:</strong> The slug is auto-generated based on the Trading Name and cannot be manually edited.</p><h3 id="saving-changes" tabindex="-1"><a class="header-anchor" href="#saving-changes"><span>Saving Changes</span></a></h3><ol><li>Make your desired changes</li><li>Click <strong>Submit</strong></li><li>Changes are saved and reflected in the companies table</li></ol><hr><h2 id="searching-and-filtering-companies" tabindex="-1"><a class="header-anchor" href="#searching-and-filtering-companies"><span>Searching and Filtering Companies</span></a></h2><h3 id="keyword-search" tabindex="-1"><a class="header-anchor" href="#keyword-search"><span>Keyword Search</span></a></h3><p>Use the search box at the top of the table to find companies by:</p><ul><li>Trading Name</li><li>Registration Number</li><li>Phone Number</li><li>Street Address</li><li>City</li><li>Province</li></ul><p>The search is instant and filters results as you type.</p><h3 id="sorting" tabindex="-1"><a class="header-anchor" href="#sorting"><span>Sorting</span></a></h3><p>Click any column header to sort the table by that field:</p><ul><li>ID (ascending/descending)</li><li>Trading Name (alphabetically)</li><li>Address (alphabetically)</li></ul><hr><h2 id="downloading-company-data" tabindex="-1"><a class="header-anchor" href="#downloading-company-data"><span>Downloading Company Data</span></a></h2><p>Click the <strong>&quot;Download&quot;</strong> button to export all company records to CSV format. This is useful for:</p><ul><li>External reporting</li><li>Data backup</li><li>Analysis in spreadsheet applications</li><li>Sharing with stakeholders</li></ul><hr><h2 id="understanding-company-fields" tabindex="-1"><a class="header-anchor" href="#understanding-company-fields"><span>Understanding Company Fields</span></a></h2><h3 id="trading-name-vs-legal-name" tabindex="-1"><a class="header-anchor" href="#trading-name-vs-legal-name"><span>Trading Name vs Legal Name</span></a></h3><ul><li><strong>Trading Name</strong>: The name the company uses for business operations</li><li>This may differ from the legal registered name</li><li>Use the name most commonly recognized by customers</li></ul><h3 id="slug" tabindex="-1"><a class="header-anchor" href="#slug"><span>Slug</span></a></h3><ul><li>Auto-generated URL-friendly version of the Trading Name</li><li>Uses lowercase letters and hyphens</li><li>Cannot contain spaces or special characters</li><li>Used in system URLs and references</li></ul><h3 id="vat-registration" tabindex="-1"><a class="header-anchor" href="#vat-registration"><span>VAT Registration</span></a></h3><ul><li>Only relevant for tax-registered companies</li><li>Check the box to enable VAT number entry</li><li>Leave unchecked for non-VAT registered entities</li></ul><h3 id="organisation-type" tabindex="-1"><a class="header-anchor" href="#organisation-type"><span>Organisation Type</span></a></h3><p>Choose the type that best describes the company&#39;s legal structure:</p><ul><li><strong>Sole Proprietorship</strong>: Single-owner business</li><li><strong>Partnership</strong>: Business owned by two or more partners</li><li><strong>Private Company</strong>: Private limited company (Pty Ltd)</li><li><strong>Public Company</strong>: Publicly traded company</li><li><strong>Non-Profit Organization</strong>: NPO or NGO</li><li><strong>Government Entity</strong>: Government department or agency</li><li><strong>Other</strong>: Any other structure</li></ul><h3 id="website-and-tld" tabindex="-1"><a class="header-anchor" href="#website-and-tld"><span>Website and TLD</span></a></h3><ul><li><strong>Website</strong>: Full URL including http:// or https://</li><li><strong>TLD</strong>: Top-level domain automatically extracted (e.g., .com, .co.za, .org)</li><li>TLD helps categorize and identify company web presence</li></ul><hr><h2 id="address-search-feature" tabindex="-1"><a class="header-anchor" href="#address-search-feature"><span>Address Search Feature</span></a></h2><h3 id="how-it-works" tabindex="-1"><a class="header-anchor" href="#how-it-works"><span>How It Works</span></a></h3><p>The address search uses Google Places API to provide accurate, standardized addresses.</p><p><strong>Local Search</strong> (Default):</p><ul><li>Searches addresses in your country</li><li>Faster and more relevant results for local companies</li><li>Recommended for domestic businesses</li></ul><p><strong>Global Search</strong>:</p><ul><li>Enable when adding international companies</li><li>Searches worldwide addresses</li><li>Takes slightly longer but covers all countries</li></ul><p><strong>Geocoding Benefits</strong>:</p><ul><li>Captures exact GPS coordinates</li><li>Enables mapping features</li><li>Allows distance calculations</li><li>Supports location-based reporting</li></ul><h3 id="best-practices-for-address-search" tabindex="-1"><a class="header-anchor" href="#best-practices-for-address-search"><span>Best Practices for Address Search</span></a></h3><ol><li><p><strong>Be Specific</strong>: Type enough detail to narrow results</p><ul><li>Good: &quot;123 Main Street, Cape Town&quot;</li><li>Less Good: &quot;Main Street&quot;</li></ul></li><li><p><strong>Select from Suggestions</strong>: Always choose from dropdown</p><ul><li>Don&#39;t manually type the full address</li><li>Ensures proper geocoding</li></ul></li><li><p><strong>Verify Address</strong>: Check the displayed address after selection</p><ul><li>Confirm it matches the intended location</li><li>Edit if necessary using Change Address feature</li></ul></li></ol><hr><h2 id="common-workflows" tabindex="-1"><a class="header-anchor" href="#common-workflows"><span>Common Workflows</span></a></h2><h3 id="adding-a-new-client-company" tabindex="-1"><a class="header-anchor" href="#adding-a-new-client-company"><span>Adding a New Client Company</span></a></h3><ol><li>Navigate to Companies page</li><li>Click &quot;Add Company&quot;</li><li>Select Company Type: &quot;Client&quot;</li><li>Select Organisation Type: e.g., &quot;Private Company&quot;</li><li>Search and select company address</li><li>Enter Trading Name: e.g., &quot;Smith &amp; Associates Travel&quot;</li><li>Note auto-generated slug: &quot;smith-associates-travel&quot;</li><li>Enter company email and phone</li><li>Enter registration number if available</li><li>Check VAT Registered if applicable, enter VAT number</li><li>Enter website if available</li><li>Submit</li></ol><h3 id="updating-company-contact-information" tabindex="-1"><a class="header-anchor" href="#updating-company-contact-information"><span>Updating Company Contact Information</span></a></h3><ol><li>Locate company in table</li><li>Click edit icon</li><li>Update Email and/or Phone Number fields</li><li>Submit changes</li></ol><h3 id="relocating-a-company" tabindex="-1"><a class="header-anchor" href="#relocating-a-company"><span>Relocating a Company</span></a></h3><ol><li>Open company for editing</li><li>Toggle &quot;Change Address&quot; to ON</li><li>Search for new address</li><li>Select correct address from suggestions</li><li>Verify new coordinates are displayed</li><li>Submit changes</li></ol><hr><h2 id="integration-with-other-pages" tabindex="-1"><a class="header-anchor" href="#integration-with-other-pages"><span>Integration with Other pages</span></a></h2><h3 id="companies-and-contacts" tabindex="-1"><a class="header-anchor" href="#companies-and-contacts"><span>Companies and Contacts</span></a></h3><p>When creating a <strong>Company Contact</strong> in the Contacts page:</p><ul><li>You must first have the company created in this page</li><li>The contact will be linked to the company</li><li>Company details flow through to contact records</li></ul><h3 id="companies-and-leads" tabindex="-1"><a class="header-anchor" href="#companies-and-leads"><span>Companies and Leads</span></a></h3><p>When a lead is associated with a company:</p><ul><li>Company information is available in lead details</li><li>Helps qualify B2B leads</li><li>Links multiple contacts to the same organization</li></ul><h3 id="companies-and-deals" tabindex="-1"><a class="header-anchor" href="#companies-and-deals"><span>Companies and Deals</span></a></h3><p>Company records connect to deals for:</p><ul><li>B2B sales tracking</li><li>Corporate client management</li><li>Multi-contact deal coordination</li></ul><hr><h2 id="data-quality-best-practices" tabindex="-1"><a class="header-anchor" href="#data-quality-best-practices"><span>Data Quality Best Practices</span></a></h2><ol><li><p><strong>Consistent Naming</strong>: Use official company names</p><ul><li>Check company websites for correct spelling</li><li>Use proper capitalization</li></ul></li><li><p><strong>Complete Records</strong>: Fill in all available information</p><ul><li>Even optional fields add value</li><li>More data = better reporting</li></ul></li><li><p><strong>Regular Updates</strong>: Keep information current</p><ul><li>Update contact details when they change</li><li>Verify addresses periodically</li></ul></li><li><p><strong>Avoid Duplicates</strong>: Search before adding</p><ul><li>Check if company already exists</li><li>Use search function first</li></ul></li><li><p><strong>Accurate Classification</strong>: Choose correct types</p><ul><li>Organisation Type affects reporting</li><li>Company Type determines workflows</li></ul></li><li><p><strong>Verify VAT Status</strong>: Confirm before checking</p><ul><li>Important for invoicing</li><li>Affects financial records</li></ul></li></ol><hr><hr><h2 id="support" tabindex="-1"><a class="header-anchor" href="#support"><span>Support</span></a></h2><p>For additional assistance with the Companies page, contact your system administrator or refer to the main CRM documentation.</p>',108)])])}const d=a(o,[["render",s]]),c=JSON.parse('{"path":"/crm/companies.html","title":"Companies","lang":"en-US","frontmatter":{"icon":"mdi:office-building","title":"Companies","category":["Guide"],"tag":["encryption"],"contributors":false,"editLink":false,"author":false,"comment":false,"description":"Companies Management Training Guide Overview The Companies page allows you to manage all business entities in your CRM system. Companies can be clients, suppliers, partners, or ...","head":[["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"Companies\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2025-12-05T17:07:22.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Mr.Hope\\",\\"url\\":\\"https://mister-hope.com\\"}]}"],["meta",{"property":"og:url","content":"https://vuepress-theme-hope-docs-demo.netlify.app/zap_training_docs/crm/companies.html"}],["meta",{"property":"og:title","content":"Companies"}],["meta",{"property":"og:description","content":"Companies Management Training Guide Overview The Companies page allows you to manage all business entities in your CRM system. Companies can be clients, suppliers, partners, or ..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"en-US"}],["meta",{"property":"og:updated_time","content":"2025-12-05T17:07:22.000Z"}],["meta",{"property":"article:tag","content":"encryption"}],["meta",{"property":"article:modified_time","content":"2025-12-05T17:07:22.000Z"}]]},"git":{"createdTime":1764918171000,"updatedTime":1764954442000},"readingTime":{"minutes":4.35,"words":1305},"filePathRelative":"crm/companies.md","autoDesc":true}');export{d as comp,c as data};
